mapper.select:
SELECT COUNT(DISTINCT PRO.idx) FROM TBL_PROGRAM PRO JOIN TBL_PROGRAM_TAGS PRO_TAG ON PRO.idx = PRO_TAG.pro_idx JOIN TBL_TAGS TAG ON PRO_TAG.tag_idx = TAG.idx WHERE 1=1
AND TAG.name IN
#{tag}

mapper.update:
UPDATE TBL_PROGRAM SET TITLE = #{title} ,
IMAGE = #{image},
OPERATE = #{operate}, START_TM = #{start_tm}, END_TM = #{end_tm}, EXPLAN = #{explan}, CONTENT = #{content}, UPDATE_TM = now() WHERE IDX = #{idx}

mapper.select:
		SELECT idx , SUBSTRING_INDEX(name, ':', -1) AS name
    	FROM TBL_TAGS
    	WHERE name LIKE #{value}
